INSERT INTO public.item_description (features, item_id, full_description, consists, additives, analysis, microelements, additional) VALUES (null, 40, 'Корм Soft Mini с перепелкой и картофелем - это полувлажный беззерновой корм, созданный для взрослых собак мелких пород. Высокое содержание свежего мяса (не менее 50%), в качестве единственного источника протеина, особая полувлажная консистенция (19% влаги), а также отсутствие злаков в составе снижают риск пищевой непереносимости и аллергии. Легкоусвояемый картофель является источником углеводов. Форма и размер гранул специально адаптированы для собак мелких пород.', 'Свежее мясо перепелки (50%), картофельная мука (12%), гороховый белок, горох (сушеный), картофельный крахмал, льняное семя, птичий жир, гидролизованный белок (вегетарианец), сушеная свекловичная мякоть (без сахара), клетчатка, картофельный белок, дрожжи (сушеные, включая бета-глюканы), дикальцийфосфат, карбонат кальция, цикорий (сушеный), семена подорожника, хлорид натрия, хондроитин сульфат, глюкозамин.', null, null, null, null, null);
